pragma solidity 0.7.6;
pragma abicoder v2;

import "@openzeppelin/contracts/math/SafeMath.sol";
import "@uniswap/v3-core/contracts/libraries/FixedPoint96.sol";
import "@uniswap/v3-core/contracts/libraries/TickMath.sol";
import "@uniswap/v3-core/contracts/libraries/FullMath.sol";
import "@uniswap/v3-periphery/contracts/libraries/LiquidityAmounts.sol";
import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import "../interfaces/IERC20Extended.sol";
import "./SafeInt24Math.sol";
import "./SafeInt56Math.sol";
import "./MathHelper.sol";
import "./UniswapHelper.sol";

///@title library to help with swap amounts calculations
library SwapHelper {
    uint8 internal constant RESOLUTION48 = 48;
    uint256 internal constant Q48 = 0x1000000000000;

    using SafeInt24Math for int24;
    using SafeInt56Math for int56;
    using SafeMath for uint256;

    ///@notice returns the amount of token1 needed for a mint for 1e18 token0
    ///@param sqrtRatioX96 sqrt ratio of the pool
    ///@param sqrtRatioAX96 sqrt ratio of lower tick of position
    ///@param sqrtRatioBX96 sqrt ratio of upper tick of position
    ///@return ratioX96 amount1/amount0 * 2**96
    function getRatioFromRange(
        uint160 sqrtRatioX96,
        uint160 sqrtRatioAX96,
        uint160 sqrtRatioBX96
    ) internal pure returns (uint256 ratioX96) {
        require(sqrtRatioAX96 < sqrtRatioX96 && sqrtRatioBX96 > sqrtRatioX96, "SHR");
        uint128 liquidity = LiquidityAmounts.getLiquidityForAmount0(sqrtRatioX96, sqrtRatioBX96, Q48);
        (, ratioX96) = LiquidityAmounts.getAmountsForLiquidity(sqrtRatioX96, sqrtRatioAX96, sqrtRatioBX96, liquidity);
    }

    ///@notice calculate amount to be swapped in order to deposit according to the ratio selected position needs
    ///@param sqrtRatioX96 sqrt ratio of the pool
    ///@param tickLower lower tick of position
    ///@param tickUpper upper tick of position
    ///@param sqrtPriceX96 sqrt price of the pool to swap
    ///@param amount0In amount of token0 available
    ///@param amount1In amount of token1 available
    ///@return amountToSwap amount of token to be swapped
    ///@return token0In true if token0 is swapped for token1, false if token1 is swapped for token1
    function calcAmountToSwap(
        uint160 sqrtRatioX96,
        int24 tickLower,
        int24 tickUpper,
        uint160 sqrtPriceX96,
        uint256 amount0In,
        uint256 amount1In
    ) internal pure returns (uint256 amountToSwap, bool token0In) {
        require(amount0In != 0 || amount1In != 0, "SHA");

        uint160 sqrtRatioAX96 = TickMath.getSqrtRatioAtTick(tickLower);
        uint160 sqrtRatioBX96 = TickMath.getSqrtRatioAtTick(tickUpper);

        //if current price >= tickUpper, then my range is under the current tick, so my position will all be in token1
        if (sqrtRatioX96 >= sqrtRatioBX96) {
            amountToSwap = amount0In;
            token0In = true;
        }
        //if current price  <= tickUpper, then my range is over the current tick, so my position will all be in token1
        else if (sqrtRatioX96 <= sqrtRatioAX96) {
            amountToSwap = amount1In;
            token0In = false;
        } else {
            uint256 ratioX96 = getRatioFromRange(sqrtRatioX96, sqrtRatioAX96, sqrtRatioBX96);
            uint256 valueX96 = (amount0In.mul((uint256(sqrtPriceX96) ** 2) >> FixedPoint96.RESOLUTION)).add(
                amount1In << FixedPoint96.RESOLUTION
            );

            uint256 amount0Post = valueX96.div(
                ((uint256(sqrtPriceX96) ** 2) >> FixedPoint96.RESOLUTION).add(ratioX96 << RESOLUTION48)
            );
            token0In = amount0Post < amount0In;

            if (token0In) {
                amountToSwap = amount0In.sub(amount0Post);
            } else {
                amountToSwap = amount1In.sub((amount0Post).mul(ratioX96) >> RESOLUTION48);
            }
        }
    }

    ///@notice Check price volatility is under specified threshold. This mitigates price manipulation during rebalance
    ///@param pool v3 pool
    ///@param maxTwapDeviation max deviation threshold from the twap tick price
    ///@param twapDuration duration of the twap oracle observations
    function checkDeviation(IUniswapV3Pool pool, int24 maxTwapDeviation, uint32 twapDuration) internal view {
        ///NOTE: MAX_TWAP_DEVIATION = 100  # 1% , TWAP_DURATION = 60  # 60 seconds
        if (twapDuration == 0) {
            //bypass check
            return;
        }
        (, int24 currentTick, , uint16 observationCardinality, , , ) = pool.slot0();
        if (observationCardinality == 0) {
            //bypass check
            return;
        }
        int24 twap = getTwap(pool, twapDuration);
        int24 deviation = currentTick > twap ? currentTick.sub(twap) : twap.sub(currentTick);
        require(deviation <= maxTwapDeviation, "SHD");
    }

    ///@notice Fetch time-weighted average price in ticks from Uniswap pool for specified duration
    ///@param pool v3 pool
    ///@param twapDuration duration of the twap oracle observations
    function getTwap(IUniswapV3Pool pool, uint32 twapDuration) internal view returns (int24) {
        require(twapDuration > 0, "SHGT");
        uint32[] memory secondsAgo = new uint32[](2);
        secondsAgo[0] = twapDuration;
        secondsAgo[1] = 0; // 0 is the most recent observation

        (int56[] memory tickCumulatives, ) = pool.observe(secondsAgo);

        return MathHelper.fromInt56ToInt24(tickCumulatives[1].sub(tickCumulatives[0]).div(int56(twapDuration)));
    }

    function distributeTargetAmount(
        address token0,
        address token1,
        uint256 amount0,
        uint256 amount1,
        uint256 price0,
        uint256 price1,
        uint256 targetAmount
    ) internal view returns (uint256 amount0Distributed, uint256 amount1Distributed) {
        uint256 denominator = FullMath.mulDiv(amount0, price0, uint256(10 ** (IERC20Extended(token0).decimals()))).add(
            FullMath.mulDiv(amount1, price1, uint256(10 ** (IERC20Extended(token1).decimals())))
        );

        require(denominator >= targetAmount && denominator > 0, "DTA");

        amount0Distributed = FullMath.mulDiv(amount0, targetAmount, denominator);
        amount1Distributed = FullMath.mulDiv(amount1, targetAmount, denominator);
    }

    function getPrice(uint160 sqrtPriceX96, address token, address tokenQuote) internal view returns (uint256) {
        if (token == tokenQuote) {
            return getPriceWithSameToken(token);
        }
        return
            getQuoteFromSqrtRatioX96(sqrtPriceX96, uint128(10 ** IERC20Extended(token).decimals()), token, tokenQuote);
    }

    function getPriceWithSameToken(address token) internal view returns (uint256) {
        return 10 ** IERC20Extended(token).decimals();
    }

    function getQuoteFromSqrtRatioX96(
        uint160 sqrtRatioX96,
        uint128 baseAmount,
        address baseToken,
        address quoteToken
    ) internal pure returns (uint256 quoteAmount) {
        // Calculate quoteAmount with better precision if it doesn't overflow when multiplied by itself
        if (sqrtRatioX96 <= type(uint128).max) {
            uint256 ratioX192 = uint256(sqrtRatioX96) * sqrtRatioX96;
            quoteAmount = baseToken < quoteToken
                ? FullMath.mulDiv(ratioX192, baseAmount, 1 << 192)
                : FullMath.mulDiv(1 << 192, baseAmount, ratioX192);
        } else {
            uint256 ratioX128 = FullMath.mulDiv(sqrtRatioX96, sqrtRatioX96, 1 << 64);
            quoteAmount = baseToken < quoteToken
                ? FullMath.mulDiv(ratioX128, baseAmount, 1 << 128)
                : FullMath.mulDiv(1 << 128, baseAmount, ratioX128);
        }
    }

    function getQuoteFromDeepestPool(
        address factoryAddress,
        address baseToken,
        address quoteToken,
        uint256 baseAmount,
        uint24[] memory feeTiers
    ) internal view returns (uint256 quoteAmount) {
        if (baseToken == quoteToken) {
            return baseAmount;
        }
        address deepestPool = UniswapHelper.findV3DeepestPool(factoryAddress, baseToken, quoteToken, feeTiers);

        (uint160 sqrtPriceX96, , , , , , ) = IUniswapV3Pool(deepestPool).slot0();

        return
            getQuoteFromSqrtRatioX96(sqrtPriceX96, MathHelper.fromUint256ToUint128(baseAmount), baseToken, quoteToken);
    }
}
","keccak256":"0x005a65be3d703b7cc0e695d95e111c8aefbbb1c926be7e5081594ab003690b68","license":"GPL-2.0"},"contracts/libraries/UniswapHelper.sol":{"content":"// SPDX-License-Identifier: GPL-2.0

pragma solidity 0.7.6;
pragma abicoder v2;

import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import "@uniswap/v3-core/contracts/libraries/TickMath.sol";
import "@uniswap/v3-periphery/contracts/interfaces/INonfungiblePositionManager.sol";
import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Factory.sol";
import "@uniswap/v3-periphery/contracts/libraries/LiquidityAmounts.sol";
import "@uniswap/v3-core/contracts/libraries/SqrtPriceMath.sol";
import "@uniswap/v3-periphery/contracts/libraries/PoolAddress.sol";
import "./MathHelper.sol";
import "./SafeInt24Math.sol";
import "./SwapHelper.sol";

///@title library to interact with NFT token and do some useful function with it
library UniswapHelper {
    using SafeInt24Math for int24;

    ///@notice get the pool address
    ///@param factory address of the UniswapV3Factory
    ///@param token0 address of the token0
    ///@param token1 address of the token1
    ///@param fee fee tier of the pool
    ///@return address address of the pool
    function getPool(address factory, address token0, address token1, uint24 fee) internal view returns (address) {
        address pool = IUniswapV3Factory(factory).getPool(token0, token1, fee);
        require(pool != address(0), "UHP0");
        return pool;
    }

    ///@notice struct for output of the getTokens function
    ///@param token0 address of the token0
    ///@param token1 address of the token1
    ///@param fee fee tier of the pool
    ///@param tickLower of position
    ///@param tickUpper of position
    struct getTokensOutput {
        address token0;
        address token1;
        uint24 fee;
        int24 tickLower;
        int24 tickUpper;
    }

    ///@notice get the address of the tpkens from the tokenId
    ///@param tokenId id of the position (NFT)
    ///@param nonfungiblePositionManager instance of the nonfungiblePositionManager given by the caller (address)
    ///@return output getTokensOutput struct
    function getTokens(
        uint256 tokenId,
        INonfungiblePositionManager nonfungiblePositionManager
    ) internal view returns (getTokensOutput memory output) {
        (
            ,
            ,
            output.token0,
            output.token1,
            output.fee,
            output.tickLower,
            output.tickUpper,
            ,
            ,
            ,
            ,

        ) = nonfungiblePositionManager.positions(tokenId);
    }

    ///@notice get the amount of tokens from liquidity and tick ranges
    ///@param liquidity amount of liquidity to convert
    ///@param currentTick current tick
    ///@param tickLower lower tick range
    ///@param tickUpper upper tick range
    ///@param sqrtPriceX96 square root of the price
    ///@return amount0 uint256 amount of token0
    ///@return amount1 uint256 amount of token1
    function getAmountsFromLiquidity(
        uint128 liquidity,
        int24 currentTick,
        int24 tickLower,
        int24 tickUpper,
        uint160 sqrtPriceX96
    ) internal pure returns (uint256 amount0, uint256 amount1) {
        if (currentTick < tickLower) {
            // current tick is below the passed range; liquidity can only become in range by crossing from left to
            // right, when we'll need _more_ token0 (it's becoming more valuable) so user must provide it
            amount0 = SqrtPriceMath.getAmount0Delta(
                TickMath.getSqrtRatioAtTick(tickLower),
                TickMath.getSqrtRatioAtTick(tickUpper),
                liquidity,
                true
            );
        } else if (currentTick < tickUpper) {
            amount0 = SqrtPriceMath.getAmount0Delta(
                sqrtPriceX96,
                TickMath.getSqrtRatioAtTick(tickUpper),
                liquidity,
                true
            );
            amount1 = SqrtPriceMath.getAmount1Delta(
                TickMath.getSqrtRatioAtTick(tickLower),
                sqrtPriceX96,
                liquidity,
                true
            );
        } else {
            // current tick is above the passed range; liquidity can only become in range by crossing from right to
            // left, when we'll need _more_ token1 (it's becoming more valuable) so user must provide it
            amount1 = SqrtPriceMath.getAmount1Delta(
                TickMath.getSqrtRatioAtTick(tickLower),
                TickMath.getSqrtRatioAtTick(tickUpper),
                liquidity,
                true
            );
        }
    }

    ///@notice Computes the amount of liquidity for a given amount of token0, token1
    ///@param amount0 The amount of token0 being sent in
    ///@param amount1 The amount of token1 being sent in
    ///@param tickLower lower tick range
    ///@param tickUpper upper tick range
    ///@param sqrtRatioX96 square root of the ratio
    ///@return liquidity The amount of liquidity received
    function getLiquidityFromAmounts(
        uint256 amount0,
        uint256 amount1,
        int24 tickLower,
        int24 tickUpper,
        uint160 sqrtRatioX96
    ) internal pure returns (uint128 liquidity) {
        uint160 sqrtRatioAX96 = TickMath.getSqrtRatioAtTick(tickLower);
        uint160 sqrtRatioBX96 = TickMath.getSqrtRatioAtTick(tickUpper);
        uint256 amount0Desired = amount0;
        uint256 amount1Desired = amount1;

        if (sqrtRatioAX96 > sqrtRatioBX96) (sqrtRatioAX96, sqrtRatioBX96) = (sqrtRatioBX96, sqrtRatioAX96);

        if (sqrtRatioX96 <= sqrtRatioAX96) {
            liquidity = LiquidityAmounts.getLiquidityForAmount0(sqrtRatioAX96, sqrtRatioBX96, amount0Desired);
        } else if (sqrtRatioX96 < sqrtRatioBX96) {
            if (amount0Desired == 0) {
                liquidity = LiquidityAmounts.getLiquidityForAmount1(sqrtRatioAX96, sqrtRatioX96, amount1Desired);
            } else if (amount1Desired == 0) {
                liquidity = LiquidityAmounts.getLiquidityForAmount0(sqrtRatioX96, sqrtRatioBX96, amount0Desired);
            } else {
                uint128 liquidity0 = LiquidityAmounts.getLiquidityForAmount0(
                    sqrtRatioX96,
                    sqrtRatioBX96,
                    amount0Desired
                );
                uint128 liquidity1 = LiquidityAmounts.getLiquidityForAmount1(
                    sqrtRatioAX96,
                    sqrtRatioX96,
                    amount1Desired
                );
                liquidity = liquidity0 < liquidity1 ? liquidity0 : liquidity1;
            }
        } else {
            liquidity = LiquidityAmounts.getLiquidityForAmount1(sqrtRatioAX96, sqrtRatioBX96, amount1Desired);
        }
    }

    ///@notice Reorder tokens to be in the correct order for the pool
    ///@param _token0 address of token0
    ///@param _token1 address of token1
    ///@return token0 address of token0 after reordering
    ///@return token1 address of token1 after reordering
    ///@return isOrderChanged bool if the order was changed
    function reorderTokens(
        address _token0,
        address _token1
    ) internal pure returns (address token0, address token1, bool isOrderChanged) {
        if (_token0 > _token1) {
            token0 = _token1;
            token1 = _token0;
            isOrderChanged = true;
        } else {
            token0 = _token0;
            token1 = _token1;
            isOrderChanged = false;
        }
    }

    ///@notice Calculates the liquidity and amounts for a given position
    ///@param factory address of the UniswapV3Factory
    ///@param token0 address of the token0
    ///@param token1 address of the token1
    ///@param fee fee tier of the pool
    ///@param tickLower the lower bound of position
    ///@param tickUpper the upper bound of position
    ///@param amount0Desired amount of token0
    ///@param amount1Desired amount of token1
    function calLiquidityAndAmounts(
        address factory,
        address token0,
        address token1,
        uint24 fee,
        int24 tickLower,
        int24 tickUpper,
        uint256 amount0Desired,
        uint256 amount1Desired
    ) internal view returns (uint128 liquidity, uint256 amount0, uint256 amount1) {
        address poolAddress = UniswapHelper.getPool(factory, token0, token1, fee);

        IUniswapV3Pool pool = IUniswapV3Pool(poolAddress);
        (uint160 sqrtRatioX96, int24 tick, , , , , ) = pool.slot0();

        liquidity = UniswapHelper.getLiquidityFromAmounts(
            amount0Desired,
            amount1Desired,
            tickLower,
            tickUpper,
            sqrtRatioX96
        );

        (amount0, amount1) = UniswapHelper.getAmountsFromLiquidity(liquidity, tick, tickLower, tickUpper, sqrtRatioX96);
    }

    ///@notice find uniswap v3 deepest pool of specific pair
    ///@param factoryAddress address of the UniswapV3Factory
    ///@param token0 address of the token0
    ///@param token1 address of the token1
    ///@param feeTiers array of fee tiers to check
    function findV3DeepestPool(
        address factoryAddress,
        address token0,
        address token1,
        uint24[] memory feeTiers
    ) internal view returns (address deepestPool) {
        uint128 largestLiquidity;

        for (uint256 i; i < feeTiers.length; ++i) {
            if (feeTiers[i] == 0) {
                continue;
            }

            address _poolAddress = IUniswapV3Factory(factoryAddress).getPool(token0, token1, feeTiers[i]);

            if (_poolAddress == address(0)) {
                continue;
            }

            uint128 _liquidity = IUniswapV3Pool(_poolAddress).liquidity();

            (deepestPool, largestLiquidity) = _liquidity > largestLiquidity
                ? (_poolAddress, _liquidity)
                : (deepestPool, largestLiquidity);
        }

        require(deepestPool != address(0), "UHDP0");
    }

    ///@notice check if the pool exist
    ///@param factoryAddress address of the UniswapV3Factory
    ///@param token0 address of the token0
    ///@param token1 address of the token1
    ///@param feeTiers array of fee tiers to check
    function isPoolExist(
        address factoryAddress,
        address token0,
        address token1,
        uint24[] memory feeTiers
    ) internal view returns (bool) {
        for (uint256 i = 0; i < feeTiers.length; ++i) {
            if (feeTiers[i] == 0) {
                continue;
            }

            address pool = IUniswapV3Factory(factoryAddress).getPool(token0, token1, feeTiers[i]);
            if (pool != address(0)) {
                return true;
            }
        }
        return false;
    }

    function getDepositCurrentTick(
        address factoryAddress,
        address token0,
        address token1,
        uint24 fee
    ) internal view returns (int24 currentTick) {
        return adjustDepositTick(factoryAddress, getCurrentTick(factoryAddress, token0, token1, fee), fee);
    }

    function getCurrentTick(
        address factoryAddress,
        address token0,
        address token1,
        uint24 fee
    ) internal view returns (int24 currentTick) {
        address pool = IUniswapV3Factory(factoryAddress).getPool(token0, token1, fee);
        require(pool != address(0), "UHP0");

        (, currentTick, , , , , ) = IUniswapV3Pool(pool).slot0();
    }

    function adjustDepositTick(
        address factoryAddress,
        int24 currentTick,
        uint24 fee
    ) internal view returns (int24 currentTickAdjusted) {
        // fee amount tick spacing
        // 100: 1
        // 500: 10
        // 3000: 60
        // 10000: 200
        currentTickAdjusted = currentTick;
        int24 tickSpacing = IUniswapV3Factory(factoryAddress).feeAmountTickSpacing(fee);
        if (tickSpacing > 1) {
            if (currentTick > 0) {
                int24 half = tickSpacing.div(2);
                int24 remainder = currentTick.mod(tickSpacing);
                if (remainder >= half) {
                    currentTickAdjusted = currentTick.sub(remainder).add(tickSpacing);
                } else {
                    currentTickAdjusted = currentTick.sub(remainder);
                }
            } else if (currentTick < 0) {
                int24 half = -tickSpacing.div(2);
                int24 remainder = currentTick.mod(tickSpacing);
                if (remainder <= half) {
                    currentTickAdjusted = currentTick.sub(remainder).sub(tickSpacing);
                } else {
                    currentTickAdjusted = currentTick.sub(remainder);
                }
            }
        }
    }

    function isPoolValid(
        address factoryAddress,
        address pool,
        address weth9,
        address usdValue,
        uint24[] memory feeTiers
    ) internal view returns (bool) {
        (, , , , , , bool unlocked) = IUniswapV3Pool(pool).slot0();
        if (!unlocked) {
            return false;
        }
        return
            checkTokenCanBeSwapToWETH9(factoryAddress, IUniswapV3Pool(pool).token0(), weth9, usdValue, feeTiers) &&
            checkTokenCanBeSwapToWETH9(factoryAddress, IUniswapV3Pool(pool).token1(), weth9, usdValue, feeTiers);
    }

    function checkTokenCanBeSwapToWETH9(
        address factoryAddress,
        address token,
        address weth9,
        address usdValue,
        uint24[] memory feeTiers
    ) internal view returns (bool) {
        if (token == weth9 || token == usdValue) {
            return true;
        }
        return UniswapHelper.isPoolExist(factoryAddress, token, usdValue, feeTiers);
    }
}
