pragma solidity 0.7.6;
pragma abicoder v2;

import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import "@uniswap/v3-periphery/contracts/interfaces/INonfungiblePositionManager.sol";
import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Factory.sol";
import "@uniswap/v3-core/contracts/libraries/FullMath.sol";
import "@uniswap/v3-core/contracts/libraries/FixedPoint128.sol";

///@title library to interact with NFT token and do some useful function with it
library UniswapUncollectedFeeHelper {
    ///@notice the output struct of getUncollectedFees
    ///@param token0 address of the token0
    ///@param token1 address of the token1
    ///@param amount0 amount of token0
    ///@param amount1 amount of token1
    struct GetUncollectedFeesOutput {
        address token0;
        address token1;
        uint128 amount0;
        uint128 amount1;
    }

    ///@notice Get the uncollected fees of a position
    ///@param factoryAddress address of the factory
    ///@param nonfungiblePositionManagerAddress address of the nonfungiblePositionManager
    ///@param tokenId Id of the position
    ///@return output the output struct of getUncollectedFees
    function getUncollectedFees(
        address factoryAddress,
        address nonfungiblePositionManagerAddress,
        uint256 tokenId
    ) internal view returns (GetUncollectedFeesOutput memory output) {
        {
            uint128 liquidity;
            uint256 feeGrowthInside0LastX128;
            uint256 feeGrowthInside1LastX128;
            (
                ,
                ,
                output.token0,
                output.token1,
                ,
                ,
                ,
                liquidity,
                feeGrowthInside0LastX128,
                feeGrowthInside1LastX128,
                output.amount0,
                output.amount1
            ) = INonfungiblePositionManager(nonfungiblePositionManagerAddress).positions(tokenId);

            (uint256 feeGrowthInside0X128, uint256 feeGrowthInside1X128) = _getFeeGrowthInside(
                factoryAddress,
                nonfungiblePositionManagerAddress,
                tokenId
            );

            output.amount0 += uint128(
                FullMath.mulDiv(feeGrowthInside0X128 - feeGrowthInside0LastX128, liquidity, FixedPoint128.Q128)
            );
            output.amount1 += uint128(
                FullMath.mulDiv(feeGrowthInside1X128 - feeGrowthInside1LastX128, liquidity, FixedPoint128.Q128)
            );
        }
    }

    function _getFeeGrowthInside(
        address factoryAddress,
        address nonfungiblePositionManagerAddress,
        uint256 tokenId
    ) internal view returns (uint256 feeGrowthInside0X128, uint256 feeGrowthInside1X128) {
        GetFeeGrowthInsideData memory data = _preLoadDataForGetFeeGrowthInside(
            factoryAddress,
            nonfungiblePositionManagerAddress,
            tokenId
        );

        // calculate fee growth below
        uint256 feeGrowthBelow0X128;
        uint256 feeGrowthBelow1X128;
        if (data.tickCurrent >= data.tickLower) {
            feeGrowthBelow0X128 = data.lowerFeeGrowthOutside0X128;
            feeGrowthBelow1X128 = data.lowerFeeGrowthOutside1X128;
        } else {
            feeGrowthBelow0X128 = data.feeGrowthGlobal0X128 - data.lowerFeeGrowthOutside0X128;
            feeGrowthBelow1X128 = data.feeGrowthGlobal1X128 - data.lowerFeeGrowthOutside1X128;
        }

        // calculate fee growth above
        uint256 feeGrowthAbove0X128;
        uint256 feeGrowthAbove1X128;
        if (data.tickCurrent < data.tickUpper) {
            feeGrowthAbove0X128 = data.upperFeeGrowthOutside0X128;
            feeGrowthAbove1X128 = data.upperFeeGrowthOutside1X128;
        } else {
            feeGrowthAbove0X128 = data.feeGrowthGlobal0X128 - data.upperFeeGrowthOutside0X128;
            feeGrowthAbove1X128 = data.feeGrowthGlobal1X128 - data.upperFeeGrowthOutside1X128;
        }

        feeGrowthInside0X128 = data.feeGrowthGlobal0X128 - feeGrowthBelow0X128 - feeGrowthAbove0X128;
        feeGrowthInside1X128 = data.feeGrowthGlobal1X128 - feeGrowthBelow1X128 - feeGrowthAbove1X128;
    }

    struct GetFeeGrowthInsideData {
        int24 tickCurrent;
        int24 tickLower;
        int24 tickUpper;
        uint256 feeGrowthGlobal0X128; // SLOAD for gas optimization
        uint256 feeGrowthGlobal1X128; // SLOAD for gas optimization
        uint256 lowerFeeGrowthOutside0X128;
        uint256 lowerFeeGrowthOutside1X128;
        uint256 upperFeeGrowthOutside0X128;
        uint256 upperFeeGrowthOutside1X128;
    }

    function _preLoadDataForGetFeeGrowthInside(
        address factoryAddress,
        address nonfungiblePositionManagerAddress,
        uint256 tokenId
    ) internal view returns (GetFeeGrowthInsideData memory data) {
        {
            address token0;
            address token1;
            uint24 fee;
            (, , token0, token1, fee, data.tickLower, data.tickUpper, , , , , ) = INonfungiblePositionManager(
                nonfungiblePositionManagerAddress
            ).positions(tokenId);

            IUniswapV3Pool _pool = IUniswapV3Pool(
                PoolAddress.computeAddress(factoryAddress, PoolAddress.getPoolKey(token0, token1, fee))
            );

            data.feeGrowthGlobal0X128 = _pool.feeGrowthGlobal0X128(); // SLOAD for gas optimization
            data.feeGrowthGlobal1X128 = _pool.feeGrowthGlobal1X128(); // SLOAD for gas optimization

            (, data.tickCurrent, , , , , ) = _pool.slot0();
            (, , data.lowerFeeGrowthOutside0X128, data.lowerFeeGrowthOutside1X128, , , , ) = _pool.ticks(
                data.tickLower
            );

            (, , data.upperFeeGrowthOutside0X128, data.upperFeeGrowthOutside1X128, , , , ) = _pool.ticks(
                data.tickUpper
            );
        }
    }
}
","keccak256":"0x85df70b869529c787a61ee62a1795c3b96e2396ee0ac21bf6e05592fbedd1589","license":"GPL-2.0"},"contracts/modules/BaseModule.sol":{"content":"// SPDX-License-Identifier: GPL-2.0

pragma solidity 0.7.6;
pragma abicoder v2;

import "../interfaces/IRegistry.sol";
import "../interfaces/IRegistryAddressHolder.sol";
import "../interfaces/IPositionManager.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

contract BaseModule is Pausable {
    IRegistryAddressHolder public registryAddressHolder;

    modifier onlyWhitelistedKeeper() {
        require(registry().whitelistedKeepers(msg.sender), "BMOWLK");
        _;
    }

    modifier onlyGovernance() {
        require(msg.sender == registry().governance(), "BMOG");
        _;
    }

    constructor(address _registryAddressHolder) {
        require(_registryAddressHolder != address(0), "BMRAH0");
        registryAddressHolder = IRegistryAddressHolder(_registryAddressHolder);
    }

    ///@notice get IRegistry from registryAddressHolder
    ///@return IRegistry interface of registry
    function registry() internal view returns (IRegistry) {
        return IRegistry(registryAddressHolder.registry());
    }

    function pause() external onlyGovernance {
        _pause();
    }

    function unpause() external onlyGovernance {
        _unpause();
    }
}
","keccak256":"0xc5b960105980f96129201014931633e0d8fab75a842b475e6508e75ce70eee9c","license":"GPL-2.0"},"contracts/modules/IdleLiquidityModuleV2.sol":{"content":"// SPDX-License-Identifier: GPL-2.0

pragma solidity 0.7.6;
pragma abicoder v2;

import "@uniswap/v3-periphery/contracts/interfaces/INonfungiblePositionManager.sol";
import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "./BaseModule.sol";
import "../libraries/SafeInt24Math.sol";
import "../libraries/UniswapHelper.sol";
import "../libraries/UniswapUncollectedFeeHelper.sol";
import "../libraries/MathHelper.sol";
import "../interfaces/modules/IIdleLiquidityModuleV2.sol";
import "../interfaces/IPositionManagerFactory.sol";
import "../interfaces/IPositionManager.sol";
import "../interfaces/IUniswapAddressHolder.sol";
import "../interfaces/actions/IClosePositionOneShot.sol";
import "../interfaces/actions/IRepayRebalanceFeeFromManager.sol";
import "../interfaces/actions/ISwapToPositionRatio.sol";
import "../interfaces/actions/IMint.sol";
import "../base/Multicall.sol";

///@title Idle Liquidity Module to manage liquidity for a user position
contract IdleLiquidityModuleV2 is BaseModule, IIdleLiquidityModuleV2, Multicall {
    ///@notice uniswap address holder
    IUniswapAddressHolder public immutable uniswapAddressHolder;
    using SafeMath for uint256;
    using SafeInt24Math for int24;

    ///@notice assing the uniswap address holder to the contract
    ///@param _registryAddressHolder address of the registry address holder
    ///@param _uniswapAddressHolder address of the uniswap address holder
    constructor(address _registryAddressHolder, address _uniswapAddressHolder) BaseModule(_registryAddressHolder) {
        require(_uniswapAddressHolder != address(0), "ILCA0");

        uniswapAddressHolder = IUniswapAddressHolder(_uniswapAddressHolder);
    }

    ///@notice check if the position is out of range and rebalance it by swapping the tokens as necessary
    ///@param input RebalanceInput struct
    function rebalance(RebalanceInput calldata input) external whenNotPaused onlyWhitelistedKeeper {
        address positionManager = IPositionManagerFactory(registry().positionManagerFactoryAddress())
            .userToPositionManager(input.userAddress);
        require(positionManager != address(0), "ILPM0");

        IPositionManager.PositionInfo memory pInfo = IPositionManager(positionManager).getPositionInfo(
            input.positionId
        );

        checkCurrentTickOutOfRange(pInfo.tokenId);

        _CloseAndRepayRebalanceResult memory carRes = _closedAndRepayRebalance(
            _CloseAndRepayRebalanceParams({
                positionManager: positionManager,
                feeReceiver: input.feeReceiver,
                tokenId: pInfo.tokenId,
                rebalanceFee: input.estimatedGasFee
            })
        );

        uint256 amount0Desired = carRes.amount0Removed.add(carRes.amount0CollectedFee).add(pInfo.amount0Leftover);
        uint256 amount1Desired = carRes.amount1Removed.add(carRes.amount1CollectedFee).add(pInfo.amount1Leftover);

        require(amount0Desired > 0 || amount1Desired > 0, "ILAR");

        _SwapAndMintResult memory samRes = _swapAndMint(
            _SwapAndMintParams({
                positionManager: positionManager,
                tokenId: pInfo.tokenId,
                amount0: amount0Desired,
                amount1: amount1Desired,
                tickLowerDiff: pInfo.tickLowerDiff,
                tickUpperDiff: pInfo.tickUpperDiff
            })
        );

        IPositionManager(positionManager).middlewareRebalance(
            input.positionId,
            samRes.newTokenId,
            pInfo.tickLowerDiff,
            pInfo.tickUpperDiff,
            pInfo.amount0CollectedFee.add(carRes.amount0CollectedFee),
            pInfo.amount1CollectedFee.add(carRes.amount1CollectedFee),
            samRes.amount0Leftover,
            samRes.amount1Leftover
        );

        emit PositionRebalanced(
            positionManager,
            input.positionId,
            pInfo.tokenId,
            samRes.newTokenId,
            carRes.amount0Removed,
            carRes.amount1Removed,
            carRes.amount0CollectedFee,
            carRes.amount1CollectedFee,
            input.estimatedGasFee
        );
    }

    ///@notice check if the position is out of range and rebalance it by swapping the tokens as necessary and customize lower & upper tick diff
    ///@param input RebalanceWithTickDiffsInput struct
    function rebalanceWithTickDiffs(
        RebalanceWithTickDiffsInput calldata input
    ) external whenNotPaused onlyWhitelistedKeeper {
        address positionManager = IPositionManagerFactory(registry().positionManagerFactoryAddress())
            .userToPositionManager(input.userAddress);
        require(positionManager != address(0), "ILPM0");

        IPositionManager.PositionInfo memory pInfo = IPositionManager(positionManager).getPositionInfo(
            input.positionId
        );

        checkDiffOfTicksRange(input.tickLowerDiff, input.tickUpperDiff, pInfo.tokenId);
        checkCurrentTickOutOfRange(pInfo.tokenId);

        _CloseAndRepayRebalanceResult memory carRes = _closedAndRepayRebalance(
            _CloseAndRepayRebalanceParams({
                positionManager: positionManager,
                feeReceiver: input.feeReceiver,
                tokenId: pInfo.tokenId,
                rebalanceFee: input.estimatedGasFee
            })
        );

        uint256 amount0Desired = carRes.amount0Removed.add(carRes.amount0CollectedFee).add(pInfo.amount0Leftover);
        uint256 amount1Desired = carRes.amount1Removed.add(carRes.amount1CollectedFee).add(pInfo.amount1Leftover);

        require(amount0Desired > 0 || amount1Desired > 0, "ILAR");

        _SwapAndMintResult memory samRes = _swapAndMint(
            _SwapAndMintParams({
                positionManager: positionManager,
                tokenId: pInfo.tokenId,
                amount0: amount0Desired,
                amount1: amount1Desired,
                tickLowerDiff: input.tickLowerDiff,
                tickUpperDiff: input.tickUpperDiff
            })
        );

        IPositionManager(positionManager).middlewareRebalance(
            input.positionId,
            samRes.newTokenId,
            input.tickLowerDiff,
            input.tickUpperDiff,
            pInfo.amount0CollectedFee.add(carRes.amount0CollectedFee),
            pInfo.amount1CollectedFee.add(carRes.amount1CollectedFee),
            samRes.amount0Leftover,
            samRes.amount1Leftover
        );

        emit PositionRebalanced(
            positionManager,
            input.positionId,
            pInfo.tokenId,
            samRes.newTokenId,
            carRes.amount0Removed,
            carRes.amount1Removed,
            carRes.amount0CollectedFee,
            carRes.amount1CollectedFee,
            input.estimatedGasFee
        );
    }

    function _closedAndRepayRebalance(
        _CloseAndRepayRebalanceParams memory params
    ) internal returns (_CloseAndRepayRebalanceResult memory res) {
        ///@dev call getUncollectedFees
        UniswapUncollectedFeeHelper.GetUncollectedFeesOutput memory ucfOutput = UniswapUncollectedFeeHelper
            .getUncollectedFees(
                uniswapAddressHolder.uniswapV3FactoryAddress(),
                uniswapAddressHolder.nonfungiblePositionManagerAddress(),
                params.tokenId
            );

        ///@dev call closePositionOneShotAction
        IClosePositionOneShot.ClosePositionOneShotOutput memory cposOutput = IClosePositionOneShot(
            params.positionManager
        ).closePositionOneShot(
                IClosePositionOneShot.ClosePositionOneShotInput({ tokenId: params.tokenId, returnTokenToUser: false })
            );

        require(
            cposOutput.amount0Collected >= ucfOutput.amount0 && cposOutput.amount1Collected >= ucfOutput.amount1,
            "IL2CP"
        );

        if (params.rebalanceFee != 0) {
            ///@dev call repayRebalanceFeeFromManager action
            IRepayRebalanceFeeFromManager(params.positionManager).repayRebalanceFeeFromManager(
                IRepayRebalanceFeeFromManager.RepayRebalanceFeeFromManagerInput({
                    rebalanceFee: params.rebalanceFee,
                    receiver: params.feeReceiver
                })
            );
        }

        res.amount0CollectedFee = ucfOutput.amount0;
        res.amount1CollectedFee = ucfOutput.amount1;
        res.amount0Removed = cposOutput.amount0Collected.sub(ucfOutput.amount0);
        res.amount1Removed = cposOutput.amount1Collected.sub(ucfOutput.amount1);
    }

    function _swapAndMint(_SwapAndMintParams memory params) internal returns (_SwapAndMintResult memory res) {
        UniswapHelper.getTokensOutput memory tokensOutput = UniswapHelper.getTokens(
            params.tokenId,
            INonfungiblePositionManager(uniswapAddressHolder.nonfungiblePositionManagerAddress())
        );

        int24 currentTick = UniswapHelper.getDepositCurrentTick(
            uniswapAddressHolder.uniswapV3FactoryAddress(),
            tokensOutput.token0,
            tokensOutput.token1,
            tokensOutput.fee
        );

        uint256 amount0Deposit;
        uint256 amount1Deposit;
        ///@dev call swapToPositionAction to perform the swap
        (uint256 amount0AfterSwapped, uint256 amount1AfterSwapped) = ISwapToPositionRatio(params.positionManager)
            .swapToPositionRatio(
                ISwapToPositionRatio.SwapToPositionInput({
                    token0Address: tokensOutput.token0,
                    token1Address: tokensOutput.token1,
                    fee: tokensOutput.fee,
                    amount0In: params.amount0,
                    amount1In: params.amount1,
                    tickLower: currentTick.add(params.tickLowerDiff),
                    tickUpper: currentTick.add(params.tickUpperDiff)
                })
            );

        ///@dev call mintAction
        (res.newTokenId, amount0Deposit, amount1Deposit) = IMint(params.positionManager).mint(
            IMint.MintInput({
                token0Address: tokensOutput.token0,
                token1Address: tokensOutput.token1,
                fee: tokensOutput.fee,
                tickLower: currentTick.add(params.tickLowerDiff),
                tickUpper: currentTick.add(params.tickUpperDiff),
                amount0Desired: amount0AfterSwapped,
                amount1Desired: amount1AfterSwapped,
                isReturnLeftOver: false
            })
        );

        ///@dev cal leftOver
        res.amount0Leftover = amount0AfterSwapped.sub(amount0Deposit);
        res.amount1Leftover = amount1AfterSwapped.sub(amount1Deposit);
    }

    function checkCurrentTickOutOfRange(uint256 tokenId) internal view {
        UniswapHelper.getTokensOutput memory tokensOutput = UniswapHelper.getTokens(
            tokenId,
            INonfungiblePositionManager(uniswapAddressHolder.nonfungiblePositionManagerAddress())
        );

        int24 currentTick = UniswapHelper.getCurrentTick(
            uniswapAddressHolder.uniswapV3FactoryAddress(),
            tokensOutput.token0,
            tokensOutput.token1,
            tokensOutput.fee
        );

        ///@dev check if the current tick is out of range
        ///note current tick == tickUpper is out of range
        require(currentTick < tokensOutput.tickLower || currentTick >= tokensOutput.tickUpper, "ILOOR");
    }

    function checkDiffOfTicksRange(int24 tickLowerDiff, int24 tickUpperDiff, uint256 tokenId) internal view {
        UniswapHelper.getTokensOutput memory tokensOutput = UniswapHelper.getTokens(
            tokenId,
            INonfungiblePositionManager(uniswapAddressHolder.nonfungiblePositionManagerAddress())
        );
        int24 tickSpacing = IUniswapV3Factory(uniswapAddressHolder.uniswapV3FactoryAddress()).feeAmountTickSpacing(
            tokensOutput.fee
        );
        require(
            tickLowerDiff <= 0 &&
                tickUpperDiff >= 0 &&
                tickLowerDiff % tickSpacing == 0 &&
                tickUpperDiff % tickSpacing == 0,
            "ILTD"
        );
    }
}
